package com.arf.axd.service.impl;import java.util.Date;import java.util.List;import java.util.Map;import javax.annotation.Resource;import org.springframework.stereotype.Service;import com.arf.axd.dao.IEccPackageConfigDao;import com.arf.axd.entity.EccPackageConfig;import com.arf.axd.entity.EccPackageConfig.Status;import com.arf.axd.service.IEccPackageConfigService;import com.arf.core.dao.BaseDao;import com.arf.core.service.impl.BaseServiceImpl;@Service("eccPackageConfigServiceImpl")public class EccPackageConfigServiceImpl extends BaseServiceImpl<EccPackageConfig, Long> implements IEccPackageConfigService {	@Resource(name = "eccPackageConfigDaoImpl")	private IEccPackageConfigDao eccPackageConfigDaoImpl;		@Override	protected BaseDao<EccPackageConfig, Long> getBaseDao() {		return eccPackageConfigDaoImpl;	}	@Override	public List<Map<String,Object>> findByStatus(Status status,Date date) {		return eccPackageConfigDaoImpl.findByStatus(status,date);	}}