package com.arf.carbright.dao;import java.util.Date;import java.util.HashMap;import java.util.List;import com.arf.carbright.entity.Box;import com.arf.core.dao.BaseDao;public interface BoxDao extends BaseDao<Box, Long> {		/**	 * 通过使用状态来查找	 * @param communityNumber	 * @param status	 * @return	 */	List<Box> findByUseStatus(String communityNumber, Box.UseStatus status,String cabinetNum);	/**	 * 通过蓝牙柜子的编码来查找	 * @param communityNumber	 * @param cabinet	 * @param boxNum	 * @return	 */	Box findByNumber(String communityNumber,String cabinetNum,String boxNum);		/**	 * 通过柜子编号查找小箱子	 * @param cabinetNumber	 * @return	 */	List<Box> findByCabinetNumber(String cabinetNumber);	/**	 * 通过柜子编号查找小箱子	 * @param cabinetNumber	 * @return	 */	List<Box> findByCabinetBox(String communityNumber,String cabinetNum,String boxNum);		/**根据小区及柜子编号统计可用箱子个数	 * @param communityNumber 小区编号	 * @param cabinetNum 柜子编号	 * @return	 */	List<HashMap<String,Object>> cabinetBoxUsedCount(String communityNumber,String cabinetNum);		/**	 * 通过使用状态来查找	 * @param communityNumber	 * @param status	 * @return	 */	List<Box> findByUseStatus(Box.UseStatus status);		/**	 * 释放before时间节点前被占用的小箱子	 * @param before	 */	public void releaseUsedBoxes(Date before);}