package com.arf.carbright.service;import java.util.Date;import java.util.HashMap;import java.util.List;import com.arf.carbright.entity.Box;import com.arf.core.service.BaseService;public interface BoxService extends BaseService<Box, Long> {	/**	 * 找出一个可用的小箱子	 * 	 * @param communityNumber	 *            小区编码 对应的DB 字段为parkingId	 * @return	 */	public Box findAvailableOne(String communityNumber,String cabinetNum);	/**	 * 通过蓝牙箱子的编码来查找	 * @param communityNumber	 * @param cabinetNum	 * @param boxNum	 * @return	 */	public Box findByNumber(String communityNumber,String cabinetNum,String boxNum);		/**	 * 通过柜子编号查找小箱子	 * @param cabinetNumber	 * @return	 */	public List<Box> findByCabinetNumber(String cabinetNumber);		/**	 * 通过柜子编号查找小箱子	 * @param cabinetNumber	 * @return	 */	List<Box> findByCabinetBox(String communityNumber,String cabinetNum,String boxNum);		/**	 * 查询小区的所有boxes	 * @param communityNum	 * @return	 */	public List<Box> findAllByCommunityNum(String communityNum);		/**根据小区及柜子编号统计可用箱子个数	 * @param communityNumber 小区编号	 * @param cabinetNum 柜子编号	 * @return	 */	public List<HashMap<String, Object>> cabinetBoxUsedCount(String communityNumber, String cabinetNum);		/**	 * 释放before时间节点前被占用的小箱子	 * @param before	 */	public void releaseUsedBoxes(Date before);}